mapper.select:
SELECT COALESCE(SUM(amount), 0.0)
FROM yuan_transactions
WHERE user_id = #{userId}

SELECT id, user_id AS userId, amount, description, created_at AS createdAt FROM yuan_transactions
AND user_id = #{userId}
AND created_at >= #{startDate}
AND created_at <= #{endDate}
ORDER BY created_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(id)
FROM yuan_transactions


        AND user_id = #{userId}


        AND created_at >= #{startDate}


        AND created_at <= #{endDate}
